	public function findItemsByTag($tag) {
		$items = array();
		 
		foreach ($this->getHardcodedItems() as $k => $v) {
			$items[$k] = unserialize(base64_decode($v))->Items->Item;
		}
		 
		return $items;
	}

	public function findItemByAsin($asin) {
		$items = $this->getHardcodedItems();

		if (isset($items[$asin])) {
