	public function doInsert($asin, $tag, $data, $widget_html = null, $other = null) {
		global $wgExternalSharedDB;
		$db = wfGetDB(DB_MASTER, array(), $wgExternalSharedDB);
		
		$db->insert("avs_data",
					array(
						'asin' => $asin,
						'tag' => $tag,
						'data' => $data,
						'widget_html' => $widget_html,
						'other' => $other
					),
					__METHOD__,
					array("IGNORE")
		);
		
		$db->commit();
	}

	public function dbSelect($where = array()) {
		global $wgExternalSharedDB;
		$db = wfGetDB(DB_MASTER, array(), $wgExternalSharedDB);
		$res = $db->select('avs_data',
			array('asin', 'tag', 'data', 'widget_html', 'other'),
			$where,
			__METHOD__
		);
		
		$out = array();
		while($row = $db->fetchRow($res)) {
			$out[] = $row;
		}
		
		return $out;
	}
	
	
